document.addEventListener("DOMContentLoaded",(function(){loadSettings(),document.getElementById("save").addEventListener("click",(function(){saveSettings()})),document.getElementById("reset").addEventListener("click",(function(){resetSettings()})),document.getElementById("tune").addEventListener("change",enableSaveReset),document.getElementById("model").addEventListener("change",enableSaveReset),document.getElementById("aiCommand").addEventListener("change",enableSaveReset),document.getElementById("googleSearch").addEventListener("change",enableSaveReset),document.getElementById("initialPrompt").addEventListener("input",enableSaveReset),document.getElementById("youtubeSummary").addEventListener("change",enableSaveReset)}));const DEFAULT_SETTINGS={tune:"balance",gptModel:"gpt-3.5-turbo",youtubeSummary:!0,aiCommand:!0,googleSearch:!0,initialPrompt:"You are ChatGPT, a large language model trained by OpenAI.\nCarefully heed the user's instructions. \nDon't give Respond too Long or too short,make it summary. \nRespond using Markdown. \nYou are a part of chrome extension now that was made by myanpetra99, that You could be used anywhere around the web just type like '/ai' or '/typeai' to spawn you. \nWhen user tell you to type something or tell to someone or create a post or caption or status or write an email or write a letter about something, just give the straight answer without any extra sentences before the answer like `Sure, here's the...` or like `Sure, I'd be happy to help you write a..` and it can be the other, and don't add anything after the answer, just give straight pure answer about what the user just asked."};function loadSettings(){chrome.storage.sync.get(DEFAULT_SETTINGS,(function(e){document.getElementById("tune").value=e.tune,document.getElementById("model").value=e.gptModel,document.getElementById("aiCommand").checked=e.aiCommand,document.getElementById("googleSearch").checked=e.googleSearch,document.getElementById("youtubeSummary").checked=e.youtubeSummary,document.getElementById("initialPrompt").value=e.initialPrompt,document.getElementById("save").disabled=!0,document.getElementById("reset").disabled=!0}))}function saveSettings(){let e=document.getElementById("tune").value,t=document.getElementById("model").value,n=document.getElementById("aiCommand").checked,a=document.getElementById("googleSearch").checked,o=document.getElementById("initialPrompt").value,d=document.getElementById("youtubeSummary").checked;chrome.storage.sync.set({tune:e,gptModel:t,aiCommand:n,googleSearch:a,initialPrompt:o,youtubeSummary:d},(function(){alert("Settings saved"),document.getElementById("save").disabled=!0}))}function resetSettings(){document.getElementById("tune").value=DEFAULT_SETTINGS.tune,document.getElementById("model").value=DEFAULT_SETTINGS.gptModel,document.getElementById("aiCommand").checked=DEFAULT_SETTINGS.aiCommand,document.getElementById("googleSearch").checked=DEFAULT_SETTINGS.googleSearch,document.getElementById("initialPrompt").value=DEFAULT_SETTINGS.initialPrompt,document.getElementById("youtubeSummary").value=DEFAULT_SETTINGS.youtubeSummary,saveSettings(),document.getElementById("reset").disabled=!0}function enableSaveReset(){document.getElementById("save").disabled=!1,document.getElementById("reset").disabled=!1}